void _ProcessSubFrame::MaybeWaitOnProcessSubs(process::Waiter* waiter, runtime_asdl::StatusArray* status_array) {
  List<int>* codes = nullptr;
  List<syntax_asdl::loc_t*>* locs = nullptr;
  int i;
  int st;
  StackRoots _roots({&waiter, &status_array, &codes, &locs});

  if (this->to_wait == nullptr) {
    return ;
  }
  for (ListIter<int> it(this->to_close); !it.Done(); it.Next()) {
    int fd = it.Value();
    posix::close(fd);
  }
  codes = Alloc<List<int>>();
  locs = Alloc<List<syntax_asdl::loc_t*>>();
  i = 0;
  for (ListIter<process::Process*> it(this->to_wait); !it.Done(); it.Next(), ++i) {
    process::Process* p = it.Value();
    StackRoots _for({&p  });
    st = p->Wait(waiter);
    codes->append(st);
    locs->append(this->locs->index_(i));
  }
  status_array->codes = codes;
  status_array->locs = locs;
}

