void JobState::InitJobControl() {
  int orig_shell_pgrp;
  this->shell_pid = posix::getpid();
  orig_shell_pgrp = posix::getpgid(0);
  this->shell_pgrp = orig_shell_pgrp;
  this->shell_tty_fd = _GetTtyFd();
  if (this->shell_pgrp != this->shell_pid) {
    try {
      posix::setpgid(this->shell_pid, this->shell_pid);
      this->shell_pgrp = this->shell_pid;
    }
    catch (IOError_OSError* e) {
      this->shell_tty_fd = -1;
    }
  }
  if (this->shell_tty_fd != -1) {
    this->original_tty_pgrp = posix::tcgetpgrp(this->shell_tty_fd);
    try {
      posix::tcsetpgrp(this->shell_tty_fd, this->shell_pgrp);
    }
    catch (IOError_OSError* e) {
      this->shell_tty_fd = -1;
      this->shell_pgrp = orig_shell_pgrp;
      posix::setpgid(this->shell_pid, this->shell_pgrp);
    }
  }
}

