void PrintAst(syntax_asdl::command_t* node, arg_types::main* flag) {
  mylib::Writer* f = nullptr;
  Str* afmt = nullptr;
  format::ColorOutput* ast_f = nullptr;
  hnode_asdl::hnode_t* tree = nullptr;
  StackRoots _roots({&node, &flag, &f, &afmt, &ast_f, &tree});

  if (maybe_str_equals(flag->ast_format, str498)) {
    print_stderr(str499);
  }
  else {
    f = mylib::Stdout();
    afmt = flag->ast_format;
    if ((maybe_str_equals(afmt, str500) || maybe_str_equals(afmt, str501))) {
      ast_f = fmt::DetectConsoleOutput(f);
    }
    else {
      if ((maybe_str_equals(afmt, str502) || maybe_str_equals(afmt, str503))) {
        ast_f = Alloc<fmt::HtmlOutput>(f);
      }
      else {
        assert(0);  // AssertionError
      }
    }
    if (str_contains(afmt, str504)) {
      // if not PYTHON
      {
        tree = node->PrettyTree();
      }
      // endif MYCPP
    }
    else {
      tree = node->PrettyTree();
    }
    ast_f->FileHeader();
    fmt::PrintTree(tree, ast_f);
    ast_f->FileFooter();
    ast_f->write(str505);
  }
}

