void Transformer::TeaFunc(pnode::PNode* pnode, syntax_asdl::command__Func* out) {
  int pos;
  int typ2;
  int id_;
  StackRoots _roots({&pnode, &out});

  pos = 1;
  typ2 = pnode->GetChild(pos)->typ;
  if (ISNONTERMINAL(typ2)) {
    Tuple2<List<syntax_asdl::param*>*, syntax_asdl::Token*> tup3 = this->_FuncParams(pnode->GetChild(pos));
    out->pos_params = tup3.at0();
    out->pos_splat = tup3.at1();
    pos += 1;
  }
  id_ = pnode->GetChild(pos)->tok->id;
  if (id_ == Id::Op_RParen) {
    pos += 1;
  }
  else {
    if (id_ == Id::Op_Semi) {
      Tuple2<List<syntax_asdl::param*>*, syntax_asdl::Token*> tup4 = this->_FuncParams(pnode->GetChild((pos + 1)));
      out->named_params = tup4.at0();
      out->named_splat = tup4.at1();
      pos += 3;
    }
  }
  if (pnode->GetChild(pos)->typ == grammar_nt::type_expr_list) {
    out->return_types = this->_TypeExprList(pnode->GetChild(pos));
    pos += 1;
  }
  out->body = this->_Suite(pnode->GetChild(pos));
}

