int _PrintPacked(List<Str*>* matches, int max_match_len, int term_width, int max_lines, mylib::Writer* f) {
  int w;
  int num_per_line;
  Str* fmt = nullptr;
  int num_lines;
  bool too_many;
  int remainder;
  int i;
  Str* fmt2 = nullptr;
  int num_left;
  StackRoots _roots({&matches, &f, &fmt, &fmt2});

  w = (max_match_len + 2);
  num_per_line = max(1, ((term_width - 2) / w));
  fmt = str_concat(str_concat(str108, str(w)), str109);
  num_lines = 0;
  too_many = false;
  remainder = (num_per_line - 1);
  i = 0;
  for (ListIter<Str*> it(matches); !it.Done(); it.Next()) {
    Str* m = it.Value();
    StackRoots _for({&m  });
    if ((i % num_per_line) == 0) {
      f->write(str110);
    }
    f->write(StrFormat(fmt, m));
    if ((i % num_per_line) == remainder) {
      f->write(str111);
      num_lines += 1;
      if (num_lines == max_lines) {
        too_many = true;
        i += 1;
        break;
      }
    }
    i += 1;
  }
  if ((i % num_per_line) != 0) {
    f->write(str112);
    num_lines += 1;
  }
  if (too_many) {
    fmt2 = str_concat(str_concat(str_concat(str_concat(str_concat(ansi::BOLD, ansi::BLUE), str113), str((term_width - 2))), str114), ansi::RESET);
    num_left = (len(matches) - i);
    if (num_left) {
      f->write(StrFormat(StrFormat(fmt2, str115), num_left));
      num_lines += 1;
    }
  }
  return num_lines;
}

