void Tracer::PushMessage(Str* label, List<Str*>* argv) {
  mylib::BufWriter* buf = nullptr;
  StackRoots _roots({&label, &argv, &buf});

  buf = this->_RichTraceBegin(str280);
  if (buf) {
    buf->write(label);
    if (str_equals(label, str281)) {
      _PrintArgv(argv, buf);
    }
    else {
      if (str_equals(label, str282)) {
        _PrintArgv(argv->slice(1), buf);
      }
      else {
        if (str_equals(label, str283)) {
          _PrintArgv(argv->slice(1), buf);
        }
        else {
          buf->write(str284);
        }
      }
    }
    this->f->write(buf->getvalue());
  }
  this->_Inc();
}

