void Tracer::OnSimpleCommand(List<Str*>* argv) {
  mylib::BufWriter* buf = nullptr;
  int i;
  StackRoots _roots({&argv, &buf});

  buf = this->_ShTraceBegin();
  if (!buf) {
    return ;
  }
  if (this->exec_opts->xtrace_rich()) {
    return ;
  }
  i = 0;
  for (ListIter<Str*> it(argv); !it.Done(); it.Next(), ++i) {
    Str* arg = it.Value();
    StackRoots _for({&arg  });
    if (i != 0) {
      buf->write(str294);
    }
    buf->write(qsn::maybe_shell_encode(arg));
  }
  buf->write(str295);
  this->f->write(buf->getvalue());
}

void Tracer::OnAssignBuiltin(cmd_value::Assign* cmd_val) {
  mylib::BufWriter* buf = nullptr;
  int i;
  StackRoots _roots({&cmd_val, &buf});

  buf = this->_ShTraceBegin();
  if (!buf) {
    return ;
  }
  i = 0;
  for (ListIter<Str*> it(cmd_val->argv); !it.Done(); it.Next(), ++i) {
    Str* arg = it.Value();
    StackRoots _for({&arg  });
    if (i != 0) {
      buf->write(str296);
    }
    buf->write(arg);
  }
  for (ListIter<runtime_asdl::AssignArg*> it(cmd_val->pairs); !it.Done(); it.Next()) {
    runtime_asdl::AssignArg* pair = it.Value();
    StackRoots _for({&pair  });
    buf->write(str297);
    buf->write(pair->var_name);
    buf->write(str298);
    if (pair->rval) {
      _PrintShValue(pair->rval, buf);
    }
  }
  buf->write(str299);
  this->f->write(buf->getvalue());
}

void Tracer::OnShAssignment(runtime_asdl::lvalue_t* lval, syntax_asdl::assign_op_t op, runtime_asdl::value_t* val, int flags, runtime_asdl::scope_t which_scopes) {
  mylib::BufWriter* buf = nullptr;
  Str* left = nullptr;
  runtime_asdl::lvalue_t* UP_lval = nullptr;
  StackRoots _roots({&lval, &val, &buf, &left, &UP_lval});

  buf = this->_ShTraceBegin();
  if (!buf) {
    return ;
  }
  left = str300;
  UP_lval = lval;
  switch (lval->tag()) {
    case lvalue_e::Named: {
      lvalue::Named* lval = static_cast<lvalue::Named*>(UP_lval);
      left = lval->name;
    }
      break;
    case lvalue_e::Indexed: {
      lvalue::Indexed* lval = static_cast<lvalue::Indexed*>(UP_lval);
      left = StrFormat("%s[%d]", lval->name, lval->index);
    }
      break;
    case lvalue_e::Keyed: {
      lvalue::Keyed* lval = static_cast<lvalue::Keyed*>(UP_lval);
      left = StrFormat("%s[%s]", lval->name, qsn::maybe_shell_encode(lval->key));
    }
      break;
  }
  buf->write(left);
  buf->write(op == assign_op_e::PlusEqual ? str303 : str304);
  _PrintShValue(val, buf);
  buf->write(str305);
  this->f->write(buf->getvalue());
}

void Tracer::OnControlFlow(Str* keyword, int arg) {
  Str* prefix = nullptr;
  mylib::BufWriter* buf = nullptr;
  StackRoots _roots({&keyword, &prefix, &buf});

  if (!this->exec_opts->xtrace()) {
    return ;
  }
  prefix = this->_EvalPS4(str306);
  buf = Alloc<mylib::BufWriter>();
  buf->write(prefix);
  buf->write(keyword);
  buf->write(str307);
  buf->write(str(arg));
  buf->write(str308);
  this->f->write(buf->getvalue());
}

void Tracer::PrintSourceCode(syntax_asdl::Token* left_tok, syntax_asdl::Token* right_tok, alloc::Arena* arena) {
  mylib::BufWriter* buf = nullptr;
  Str* line = nullptr;
  int start;
  int end;
  StackRoots _roots({&left_tok, &right_tok, &arena, &buf, &line});

  buf = this->_ShTraceBegin();
  if (!buf) {
    return ;
  }
  line = left_tok->line->content;
  start = left_tok->col;
  if (left_tok->line == right_tok->line) {
    end = (right_tok->col + right_tok->length);
    buf->write(line->slice(start, end));
  }
  else {
    end = line->endswith(str309) ? -1 : len(line);
    buf->write(line->slice(start, end));
    buf->write(str310);
  }
  buf->write(str311);
  this->f->write(buf->getvalue());
}

