void Printer::Print(runtime_asdl::value_t* val, mylib::BufWriter* buf, int indent, int level) {
  Str* bracket_indent = nullptr;
  Str* item_indent = nullptr;
  Str* maybe_newline = nullptr;
  Str* maybe_space = nullptr;
  runtime_asdl::value_t* UP_val = nullptr;
  bool valid_utf8;
  int i;
  StackRoots _roots({&val, &buf, &bracket_indent, &item_indent, &maybe_newline, &maybe_space, &UP_val});

  if (indent == -1) {
    bracket_indent = str569;
    item_indent = str570;
    maybe_newline = str571;
    maybe_space = str572;
  }
  else {
    bracket_indent = this->_GetIndent((level * indent));
    item_indent = this->_GetIndent(((level + 1) * indent));
    maybe_newline = str573;
    maybe_space = str574;
  }
  UP_val = val;
  switch (val->tag()) {
    case value_e::Null: {
      buf->write(str575);
    }
      break;
    case value_e::Bool: {
      value::Bool* val = static_cast<value::Bool*>(UP_val);
      buf->write(val->b ? str576 : str577);
    }
      break;
    case value_e::Int: {
      value::Int* val = static_cast<value::Int*>(UP_val);
      buf->write(str(val->i));
    }
      break;
    case value_e::Float: {
      value::Float* val = static_cast<value::Float*>(UP_val);
      buf->write(str(val->f));
    }
      break;
    case value_e::Str: {
      value::Str* val = static_cast<value::Str*>(UP_val);
      buf->write(str578);
      valid_utf8 = qsn::EncodeRunes(val->s, qsn::BIT8_UTF8, buf);
      if (!valid_utf8) {
        ;  // pass
      }
      buf->write(str579);
    }
      break;
    case value_e::List: {
      value::List* val = static_cast<value::List*>(UP_val);
      buf->write(str580);
      buf->write(maybe_newline);
      i = 0;
      for (ListIter<runtime_asdl::value_t*> it(val->items); !it.Done(); it.Next(), ++i) {
        runtime_asdl::value_t* item = it.Value();
        StackRoots _for({&item      });
        if (i != 0) {
          buf->write(str581);
          buf->write(maybe_newline);
        }
        buf->write(item_indent);
        this->Print(item, buf, indent, (level + 1));
      }
      buf->write(maybe_newline);
      buf->write(bracket_indent);
      buf->write(str582);
    }
      break;
    case value_e::Dict: {
      value::Dict* val = static_cast<value::Dict*>(UP_val);
      buf->write(str583);
      buf->write(maybe_newline);
      i = 0;
      for (DictIter<Str*, runtime_asdl::value_t*> it(val->d); !it.Done(); it.Next()) {
        Str* k = it.Key();
        runtime_asdl::value_t* v = it.Value();
        if (i != 0) {
          buf->write(str584);
          buf->write(maybe_newline);
        }
        buf->write(item_indent);
        buf->write(str585);
        valid_utf8 = qsn::EncodeRunes(k, qsn::BIT8_UTF8, buf);
        if (!valid_utf8) {
          ;  // pass
        }
        buf->write(str586);
        buf->write(maybe_space);
        this->Print(v, buf, indent, (level + 1));
        i += 1;
      }
      buf->write(maybe_newline);
      buf->write(bracket_indent);
      buf->write(str587);
    }
      break;
    default: {
      ;  // pass
    }
  }
}

