int _PrintVariables(state::Mem* mem, cmd_value::Assign* cmd_val, args::_Attributes* attrs, bool print_flags, int builtin) {
  Dict<Str*, runtime_asdl::value_t*>* flag = nullptr;
  runtime_asdl::value_t* tmp_g = nullptr;
  runtime_asdl::value_t* tmp_a = nullptr;
  runtime_asdl::value_t* tmp_A = nullptr;
  bool flag_g;
  bool flag_a;
  bool flag_A;
  runtime_asdl::value_t* tmp_n = nullptr;
  runtime_asdl::value_t* tmp_r = nullptr;
  runtime_asdl::value_t* tmp_x = nullptr;
  Str* flag_n = nullptr;
  Str* flag_r = nullptr;
  Str* flag_x = nullptr;
  runtime_asdl::scope_t which_scopes;
  bool print_all;
  Dict<Str*, runtime_asdl::Cell*>* cells = nullptr;
  List<Str*>* names = nullptr;
  Str* name = nullptr;
  Str* s = nullptr;
  Str* invalid = nullptr;
  int count;
  runtime_asdl::Cell* cell = nullptr;
  runtime_asdl::value_t* val = nullptr;
  List<Str*>* decl = nullptr;
  List<Str*>* flags = nullptr;
  bool has_holes;
  bool first;
  int i;
  List<Str*>* body = nullptr;
  Str* key_quoted = nullptr;
  Str* value_quoted = nullptr;
  StackRoots _roots({&mem, &cmd_val, &attrs, &flag, &tmp_g, &tmp_a, &tmp_A, &tmp_n, &tmp_r, &tmp_x, &flag_n, &flag_r, &flag_x, &cells, &names, &name, &s, &invalid, &cell, &val, &decl, &flags, &body, &key_quoted, &value_quoted});

  flag = attrs->attrs;
  tmp_g = flag->get(str754);
  tmp_a = flag->get(str755);
  tmp_A = flag->get(str756);
  flag_g = (tmp_g and tmp_g->tag() == value_e::Bool) ? static_cast<value::Bool*>(tmp_g)->b : false;
  flag_a = (tmp_a and tmp_a->tag() == value_e::Bool) ? static_cast<value::Bool*>(tmp_a)->b : false;
  flag_A = (tmp_A and tmp_A->tag() == value_e::Bool) ? static_cast<value::Bool*>(tmp_A)->b : false;
  tmp_n = flag->get(str757);
  tmp_r = flag->get(str758);
  tmp_x = flag->get(str759);
  flag_n = (tmp_n and tmp_n->tag() == value_e::Str) ? static_cast<value::Str*>(tmp_n)->s : nullptr;
  flag_r = (tmp_r and tmp_r->tag() == value_e::Str) ? static_cast<value::Str*>(tmp_r)->s : nullptr;
  flag_x = (tmp_x and tmp_x->tag() == value_e::Str) ? static_cast<value::Str*>(tmp_x)->s : nullptr;
  if (cmd_val->builtin_id == builtin_i::local) {
    if ((flag_g and !mem->IsGlobalScope())) {
      return 1;
    }
    which_scopes = scope_e::LocalOnly;
  }
  else {
    if (flag_g) {
      which_scopes = scope_e::GlobalOnly;
    }
    else {
      which_scopes = mem->ScopesForReading();
    }
  }
  if (len(cmd_val->pairs) == 0) {
    print_all = true;
    cells = mem->GetAllCells(which_scopes);
    names = sorted(cells);
  }
  else {
    print_all = false;
    names = Alloc<List<Str*>>();
    cells = Alloc<Dict<Str*, runtime_asdl::Cell*>>();
    for (ListIter<runtime_asdl::AssignArg*> it(cmd_val->pairs); !it.Done(); it.Next()) {
      runtime_asdl::AssignArg* pair = it.Value();
      StackRoots _for({&pair    });
      name = pair->var_name;
      if ((pair->rval and pair->rval->tag() == value_e::Str)) {
        s = static_cast<value::Str*>(pair->rval)->s;
        invalid = StrFormat("%s=%s", name, s);
        names->append(invalid);
        cells->set(invalid, nullptr);
      }
      else {
        names->append(name);
        cells->set(name, mem->GetCell(name, which_scopes));
      }
    }
  }
  count = 0;
  for (ListIter<Str*> it(names); !it.Done(); it.Next()) {
    Str* name = it.Value();
    StackRoots _for({&name  });
    cell = cells->index_(name);
    if (cell == nullptr) {
      continue;
    }
    val = cell->val;
    if (val->tag() == value_e::Undef) {
      continue;
    }
    if ((builtin == _READONLY and !cell->readonly)) {
      continue;
    }
    if ((builtin == _EXPORT and !cell->exported)) {
      continue;
    }
    if ((maybe_str_equals(flag_n, str761) and !cell->nameref)) {
      continue;
    }
    if ((maybe_str_equals(flag_n, str762) and cell->nameref)) {
      continue;
    }
    if ((maybe_str_equals(flag_r, str763) and !cell->readonly)) {
      continue;
    }
    if ((maybe_str_equals(flag_r, str764) and cell->readonly)) {
      continue;
    }
    if ((maybe_str_equals(flag_x, str765) and !cell->exported)) {
      continue;
    }
    if ((maybe_str_equals(flag_x, str766) and cell->exported)) {
      continue;
    }
    if ((flag_a and val->tag() != value_e::BashArray)) {
      continue;
    }
    if ((flag_A and val->tag() != value_e::BashAssoc)) {
      continue;
    }
    decl = Alloc<List<Str*>>();
    if (print_flags) {
      flags = Alloc<List<Str*>>();
      if (cell->nameref) {
        flags->append(str767);
      }
      if (cell->readonly) {
        flags->append(str768);
      }
      if (cell->exported) {
        flags->append(str769);
      }
      if (val->tag() == value_e::BashArray) {
        flags->append(str770);
      }
      else {
        if (val->tag() == value_e::BashAssoc) {
          flags->append(str771);
        }
      }
      if (len(flags) == 0) {
        flags->append(str772);
      }
      decl->extend(NewList<Str*>(std::initializer_list<Str*>{str773, str774->join(flags), str775, name}));
    }
    else {
      decl->append(name);
    }
    if (val->tag() == value_e::Str) {
      value::Str* str_val = static_cast<value::Str*>(val);
      decl->extend(NewList<Str*>(std::initializer_list<Str*>{str776, qsn::maybe_shell_encode(str_val->s)}));
    }
    else {
      if (val->tag() == value_e::BashArray) {
        value::BashArray* array_val = static_cast<value::BashArray*>(val);
        has_holes = false;
        for (ListIter<Str*> it(array_val->strs); !it.Done(); it.Next()) {
          Str* s = it.Value();
          StackRoots _for({&s        });
          if (s == nullptr) {
            has_holes = true;
            break;
          }
        }
        if (has_holes) {
          decl->append(str777);
          first = true;
          i = 0;
          for (ListIter<Str*> it(array_val->strs); !it.Done(); it.Next(), ++i) {
            Str* element = it.Value();
            StackRoots _for({&element          });
            if (element != nullptr) {
              if (first) {
                decl->append(str778);
                first = false;
              }
              decl->extend(NewList<Str*>(std::initializer_list<Str*>{str779, name, str780, str(i), str781, qsn::maybe_shell_encode(element)}));
            }
          }
        }
        else {
          body = Alloc<List<Str*>>();
          for (ListIter<Str*> it(array_val->strs); !it.Done(); it.Next()) {
            Str* element = it.Value();
            StackRoots _for({&element          });
            if (len(body) > 0) {
              body->append(str782);
            }
            body->append(qsn::maybe_shell_encode(element));
          }
          decl->extend(NewList<Str*>(std::initializer_list<Str*>{str783, str784->join(body), str785}));
        }
      }
      else {
        if (val->tag() == value_e::BashAssoc) {
          value::BashAssoc* assoc_val = static_cast<value::BashAssoc*>(val);
          body = Alloc<List<Str*>>();
          for (ListIter<Str*> it(sorted(assoc_val->d)); !it.Done(); it.Next()) {
            Str* key = it.Value();
            StackRoots _for({&key          });
            if (len(body) > 0) {
              body->append(str786);
            }
            key_quoted = qsn::maybe_shell_encode(key, qsn::MUST_QUOTE);
            value_quoted = qsn::maybe_shell_encode(assoc_val->d->index_(key));
            body->extend(NewList<Str*>(std::initializer_list<Str*>{str787, key_quoted, str788, value_quoted}));
          }
          if (len(body) > 0) {
            decl->extend(NewList<Str*>(std::initializer_list<Str*>{str789, str790->join(body), str791}));
          }
        }
        else {
          ;  // pass
        }
      }
    }
    print(str792->join(decl));
    count += 1;
  }
  if ((print_all or count == len(names))) {
    return 0;
  }
  else {
    return 1;
  }
}

void _ExportReadonly(state::Mem* mem, runtime_asdl::AssignArg* pair, int flags) {
  runtime_asdl::scope_t which_scopes;
  lvalue::Named* lval = nullptr;
  runtime_asdl::value_t* old_val = nullptr;
  runtime_asdl::value_t* val = nullptr;
  StackRoots _roots({&mem, &pair, &lval, &old_val, &val});

  which_scopes = mem->ScopesForWriting();
  lval = location::LName(pair->var_name);
  if (pair->plus_eq) {
    old_val = sh_expr_eval::OldValue(lval, mem, nullptr);
    val = cmd_eval::PlusEquals(old_val, pair->rval);
  }
  else {
    val = pair->rval;
  }
  mem->SetValue(lval, val, which_scopes, flags);
}

