  source::Alias* src = nullptr;
  command::CommandList* node = nullptr;
  StackRoots _roots({&words, &aliases_in_flight, &first_word_str, &argv0_loc, &expanded, &w, &word_str, &alias_exp, &left_tok, &right_tok, &words_str, &code_str, &arena, &line_reader, &cp, &src, &node});

  aliases_in_flight = len(this->aliases_in_flight) ? this->aliases_in_flight : Alloc<List<Tuple2<Str*, int>*>>();
