bool RegexMatch(runtime_asdl::value_t* left, runtime_asdl::value_t* right, state::Mem* mem) {
  runtime_asdl::value_t* UP_right = nullptr;
  Str* right_s = nullptr;
  runtime_asdl::value_t* UP_left = nullptr;
  Str* left_s = nullptr;
  List<Str*>* matches = nullptr;
  StackRoots _roots({&left, &right, &mem, &UP_right, &right_s, &UP_left, &left_s, &matches});

  UP_right = right;
  right_s = nullptr;
  switch (right->tag()) {
    case value_e::Str: {
      value::Str* right = static_cast<value::Str*>(UP_right);
      right_s = right->s;
    }
      break;
    case value_e::Eggex: {
      value::Eggex* right = static_cast<value::Eggex*>(UP_right);
      right_s = regex_translate::AsPosixEre(right);
    }
      break;
    default: {
      throw Alloc<error::TypeErr>(right, str1957, loc::Missing);
    }
  }
  UP_left = left;
  left_s = nullptr;
  switch (left->tag()) {
    case value_e::Str: {
      value::Str* left = static_cast<value::Str*>(UP_left);
      left_s = left->s;
    }
      break;
    default: {
      throw Alloc<error::TypeErrVerbose>(str1958, loc::Missing);
    }
  }
  matches = libc::regex_match(right_s, left_s);
  if (matches != nullptr) {
    if (mem) {
      mem->SetMatches(matches);
    }
    return true;
  }
  else {
    if (mem) {
      mem->ClearMatches();
    }
    return false;
  }
}

